--------------------------------------------------------
--  DDL for Table JOBS
--------------------------------------------------------

CREATE TABLE JOBS (
  JOB_ID      VARCHAR(10) not null primary key, 
	JOB_TITLE   VARCHAR(35), 
	MIN_SALARY  INT, 
	MAX_SALARY  INT
);


--------------------------------------------------------
--  DDL for Table EMPLOYEES
--------------------------------------------------------

CREATE TABLE EMPLOYEES (	
  EMPLOYEE_ID     SERIAL UNIQUE, 
	FIRST_NAME      VARCHAR(20), 
	LAST_NAME       VARCHAR(25), 
	EMAIL           VARCHAR(25), 
	PHONE_NUMBER    VARCHAR(20), 
	HIRE_DATE       DATE, 
	JOB_ID          VARCHAR(10) REFERENCES JOBS(JOB_ID), 
	SALARY          INT, 
	COMMISSION_PCT  NUMERIC(2,2), 
	MANAGER_ID      INT REFERENCES EMPLOYEES(EMPLOYEE_ID), 
	DEPARTMENT_ID   INT 
);




--------------------------------------------------------
--  DDL for Table REGIONS
--------------------------------------------------------

CREATE TABLE REGIONS (
  REGION_ID   SERIAL UNIQUE, 
	REGION_NAME VARCHAR(25)
);


--------------------------------------------------------
--  DDL for Table COUNTRIES
--------------------------------------------------------

CREATE TABLE COUNTRIES (	
	COUNTRY_ID 	  VARCHAR(2) not null primary key,
	COUNTRY_NAME 	VARCHAR(40),
	REGION_ID		  INT REFERENCES REGIONS(REGION_ID)
);


--------------------------------------------------------
--  DDL for Table LOCATIONS
--------------------------------------------------------

CREATE TABLE LOCATIONS (
  LOCATION_ID     SERIAL UNIQUE, 
	STREET_ADDRESS  VARCHAR(40), 
	POSTAL_CODE     VARCHAR(12), 
	CITY            VARCHAR(30), 
	STATE_PROVINCE  VARCHAR(25), 
	COUNTRY_ID      CHAR(2) REFERENCES COUNTRIES(COUNTRY_ID)
);


--------------------------------------------------------
--  DDL for Table DEPARTMENTS
--------------------------------------------------------

CREATE TABLE DEPARTMENTS (
	DEPARTMENT_ID   SERIAL UNIQUE, 
	DEPARTMENT_NAME VARCHAR(30), 
	MANAGER_ID      INT REFERENCES EMPLOYEES(EMPLOYEE_ID), 
	LOCATION_ID     INT REFERENCES LOCATIONS(LOCATION_ID)
);



ALTER TABLE EMPLOYEES ADD CONSTRAINT EMP_DEPT_FK FOREIGN KEY (DEPARTMENT_ID)
	  REFERENCES DEPARTMENTS (DEPARTMENT_ID);

--------------------------------------------------------
--  DDL for Table DEPT
--------------------------------------------------------

CREATE TABLE DEPT (	
	DEPARTMENT_ID   INT REFERENCES DEPARTMENTS(DEPARTMENT_ID), 
